import{_ as e,c as r,o as t,a as o}from"./app.c6aeb2ac.js";const g=JSON.parse('{"title":"\u4E0E\u5176\u5B83\u975E\u6253\u5305\u89E3\u51B3\u65B9\u6848\u6BD4\u8F83","description":"","frontmatter":{"title":"\u4E0E\u5176\u5B83\u975E\u6253\u5305\u89E3\u51B3\u65B9\u6848\u6BD4\u8F83"},"headers":[{"level":2,"title":"Snowpack","slug":"snowpack"},{"level":2,"title":"WMR","slug":"wmr"},{"level":2,"title":"@web/dev-server","slug":"webdev-server"}],"relativePath":"guide/comparisons.md"}'),a={name:"guide/comparisons.md"},n=o('<h1 id="comparisons-with-other-no-bundler-solutions" tabindex="-1">\u4E0E\u5176\u5B83\u975E\u6253\u5305\u89E3\u51B3\u65B9\u6848\u6BD4\u8F83 <a class="header-anchor" href="#comparisons-with-other-no-bundler-solutions">\xB6</a></h1><h2 id="snowpack" tabindex="-1">Snowpack <a class="header-anchor" href="#snowpack">\xB6</a></h2><p><a href="https://www.snowpack.dev/" target="_blank" rel="noopener noreferrer">Snowpack</a> \u4E5F\u662F\u4E00\u4E2A\u4E0E Vite \u5341\u5206\u7C7B\u4F3C\u7684\u975E\u6784\u5EFA\u5F0F\u539F\u751F ESM \u5F00\u53D1\u670D\u52A1\u5668\u3002\u9664\u4E86\u4E0D\u540C\u7684\u5B9E\u73B0\u7EC6\u8282\u5916\uFF0C\u8FD9\u4E24\u4E2A\u9879\u76EE\u5728\u6280\u672F\u4E0A\u6BD4\u4F20\u7EDF\u5DE5\u5177\u6709\u5F88\u591A\u5171\u540C\u4F18\u52BF\u3002Vite \u7684\u4F9D\u8D56\u9884\u6784\u5EFA\u4E5F\u53D7\u5230\u4E86 Snowpack v1\uFF08\u73B0\u5728\u662F <a href="https://github.com/snowpackjs/snowpack/tree/main/esinstall" target="_blank" rel="noopener noreferrer"><code>esinstall</code></a>\uFF09\u7684\u542F\u53D1\u3002\u8FD9\u4E24\u4E2A\u9879\u76EE\u4E4B\u95F4\u7684\u4E00\u4E9B\u4E3B\u8981\u533A\u522B\u662F\uFF1A</p><p><strong>\u751F\u4EA7\u6784\u5EFA</strong></p><p>Snowpack \u7684\u9ED8\u8BA4\u6784\u5EFA\u8F93\u51FA\u662F\u672A\u6253\u5305\u7684\uFF1A\u5B83\u5C06\u6BCF\u4E2A\u6587\u4EF6\u8F6C\u6362\u4E3A\u5355\u72EC\u7684\u6784\u5EFA\u6A21\u5757\uFF0C\u7136\u540E\u5C06\u8FD9\u4E9B\u6A21\u5757\u63D0\u4F9B\u7ED9\u6267\u884C\u5B9E\u9645\u7ED1\u5B9A\u7684\u4E0D\u540C\u201C\u4F18\u5316\u5668\u201D\u3002\u8FD9\u4E48\u505A\u7684\u597D\u5904\u662F\uFF0C\u4F60\u53EF\u4EE5\u9009\u62E9\u4E0D\u540C\u7EC8\u7AEF\u6253\u5305\u5668\uFF0C\u4EE5\u9002\u5E94\u4E0D\u540C\u9700\u6C42\uFF08\u4F8B\u5982 webpack, Rollup\uFF0C\u751A\u81F3\u662F ESbuild\uFF09\uFF0C\u7F3A\u70B9\u662F\u4F53\u9A8C\u6709\u4E9B\u652F\u79BB\u7834\u788E \u2014\u2014 \u4F8B\u5982\uFF0C<code>esbuild</code> \u4F18\u5316\u5668\u4ECD\u7136\u662F\u4E0D\u7A33\u5B9A\u7684\uFF0CRollup \u4F18\u5316\u5668\u4E5F\u4E0D\u662F\u5B98\u65B9\u7EF4\u62A4\uFF0C\u800C\u4E0D\u540C\u7684\u4F18\u5316\u5668\u53C8\u6709\u4E0D\u540C\u7684\u8F93\u51FA\u548C\u914D\u7F6E\u3002</p><p>\u4E3A\u4E86\u63D0\u4F9B\u66F4\u6D41\u7545\u7684\u4F53\u9A8C\uFF0CVite \u9009\u62E9\u4E86\u4E0E\u5355\u4E2A\u6253\u5305\u5668\uFF08Rollup\uFF09\u8FDB\u884C\u66F4\u6DF1\u5165\u7684\u96C6\u6210\u3002Vite \u8FD8\u652F\u6301\u4E00\u5957 <a href="./api-plugin.html">\u901A\u7528\u63D2\u4EF6 API</a> \u6269\u5C55\u4E86 Rollup \u7684\u63D2\u4EF6\u63A5\u53E3\uFF0C\u5F00\u53D1\u548C\u6784\u5EFA\u4E24\u79CD\u6A21\u5F0F\u90FD\u9002\u7528\u3002</p><p>\u7531\u4E8E\u6784\u5EFA\u8FC7\u7A0B\u7684\u96C6\u6210\u5EA6\u66F4\u9AD8\uFF0CVite \u652F\u6301\u76EE\u524D\u5728 Snowpack \u6784\u5EFA\u4F18\u5316\u5668\u4E2D\u4E0D\u53EF\u7528\u7684\u591A\u79CD\u529F\u80FD\uFF1A</p><ul><li><a href="./build.html#multi-page-app">\u591A\u9875\u9762\u5E94\u7528\u652F\u6301</a></li><li><a href="./build.html#library-mode">\u5E93\u6A21\u5F0F</a></li><li><a href="./features.html#css-code-splitting">\u81EA\u52A8\u5206\u5272 CSS \u4EE3\u7801</a></li><li><a href="./features.html#async-chunk-loading-optimization">\u9884\u4F18\u5316\u7684\u5F02\u6B65 chunk \u52A0\u8F7D</a></li><li><a href="./features.html#dynamic-import-polyfill">\u5BF9\u52A8\u6001\u5BFC\u5165\u81EA\u52A8 polyfill</a></li><li>\u5B98\u65B9 <a href="https://github.com/vitejs/vite/tree/main/packages/plugin-legacy" target="_blank" rel="noopener noreferrer">\u517C\u5BB9\u6A21\u5F0F\u63D2\u4EF6</a> \u6253\u5305\u4E3A\u73B0\u4EE3/\u4F20\u7EDF\u4E24\u79CD\u4EA7\u7269\uFF0C\u5E76\u6839\u636E\u6D4F\u89C8\u5668\u652F\u6301\u81EA\u52A8\u4EA4\u4ED8\u6B63\u786E\u7684\u7248\u672C\u3002</li></ul><p><strong>\u66F4\u5FEB\u7684\u4F9D\u8D56\u9884\u6784\u5EFA</strong></p><p>Vite \u4F7F\u7528 <a href="https://esbuild.github.io/" target="_blank" rel="noopener noreferrer">esbuild</a> \u800C\u4E0D\u662F Rollup \u6765\u8FDB\u884C\u4F9D\u8D56\u9884\u6784\u5EFA\u3002\u8FD9\u4E3A\u5F00\u53D1\u670D\u52A1\u5668\u51B7\u542F\u52A8\u548C\u4F9D\u8D56\u9879\u5931\u6D3B\u7684\u91CD\u65B0\u6784\u5EFA\u65B9\u9762\u5E26\u6765\u4E86\u663E\u8457\u7684\u6027\u80FD\u6539\u8FDB\u3002</p><p><strong>Monorepo \u652F\u6301</strong></p><p>Vite \u80FD\u591F\u652F\u6301 monorepo\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u6709\u7528\u6237\u6210\u529F\u5730\u5C06 Vite \u4E0E\u57FA\u4E8E Yarn, Yarn 2 \u548C PNPM \u7684 monorepo \u4E00\u8D77\u4F7F\u7528\u3002</p><p><strong>CSS \u9884\u5904\u7406\u5668\u652F\u6301</strong></p><p>Vite \u4E3A Sass and Less \u63D0\u4F9B\u4E86\u66F4\u7CBE\u7EC6\u5316\u7684\u652F\u6301\uFF0C\u5305\u62EC\u6539\u8FDB <code>@import</code> \u89E3\u6790\uFF08\u53EF\u4F7F\u7528\u522B\u540D\u4E0E npm \u4F9D\u8D56\uFF09\u548C <a href="./features.html#import-inlining-and-rebasing">\u63D0\u4F9B <code>url()</code> \u5185\u8054\u5F15\u5165\u4E0E\u53D8\u57FA</a>\u3002</p><p><strong>Vue \u7B2C\u4E00\u4F18\u5148\u7EA7\u652F\u6301</strong></p><p>Vite \u6700\u521D\u662F\u4F5C\u4E3A <a href="https://vuejs.org/" target="_blank" rel="noopener noreferrer">Vue.js</a> \u5F00\u53D1\u5DE5\u5177\u7684\u672A\u6765\u57FA\u7840\u800C\u521B\u5EFA\u7684\u3002\u5C3D\u7BA1 Vite 2.0 \u7248\u672C\u5B8C\u5168\u4E0D\u4F9D\u8D56\u4E8E\u4EFB\u4F55\u6846\u67B6\uFF0C\u4F46\u5B98\u65B9 Vue \u63D2\u4EF6\u4ECD\u7136\u5BF9 Vue \u7684\u5355\u6587\u4EF6\u7EC4\u4EF6\u683C\u5F0F\u63D0\u4F9B\u4E86\u7B2C\u4E00\u4F18\u5148\u7EA7\u7684\u652F\u6301\uFF0C\u6DB5\u76D6\u4E86\u6240\u6709\u9AD8\u7EA7\u7279\u6027\uFF0C\u5982\u6A21\u677F\u8D44\u6E90\u5F15\u7528\u89E3\u6790\u3001<code>&lt;script setup&gt;</code>\uFF0C<code>&lt;style module&gt;</code>\uFF0C\u81EA\u5B9A\u4E49\u5757\u7B49\u7B49\u3002\u9664\u6B64\u4E4B\u5916\uFF0CVite \u8FD8\u5BF9 Vue \u5355\u6587\u4EF6\u7EC4\u4EF6\u63D0\u4F9B\u4E86\u7EC6\u7C92\u5EA6\u7684 HMR\u3002\u4E3E\u4E2A\u4F8B\u5B50\uFF0C\u66F4\u65B0\u4E00\u4E2A\u5355\u6587\u4EF6\u7EC4\u4EF6\u7684 <code>&lt;template&gt;</code> \u6216 <code>&lt;style&gt;</code> \u4F1A\u6267\u884C\u4E0D\u91CD\u7F6E\u5176\u72B6\u6001\u7684\u70ED\u66F4\u65B0\u3002</p><h2 id="wmr" tabindex="-1">WMR <a class="header-anchor" href="#wmr">\xB6</a></h2><p>Preact \u56E2\u961F\u7684 <a href="https://github.com/preactjs/wmr" target="_blank" rel="noopener noreferrer">WMR</a> \u63D0\u4F9B\u4E86\u7C7B\u4F3C\u7684\u7279\u6027\u96C6\uFF0C\u800C Vite 2.0 \u5BF9 Rollup \u63D2\u4EF6\u63A5\u53E3\u7684\u652F\u6301\u6B63\u662F\u53D7\u5230\u4E86\u5B83\u7684\u542F\u53D1\u3002</p><p>WMR \u4E3B\u8981\u662F\u4E3A\u4E86 <a href="https://preactjs.com/" target="_blank" rel="noopener noreferrer">Preact</a> \u9879\u76EE\u800C\u8BBE\u8BA1\uFF0C\u5E76\u4E3A\u5176\u63D0\u4F9B\u4E86\u96C6\u6210\u5EA6\u66F4\u9AD8\u7684\u529F\u80FD\uFF0C\u6BD4\u5982\u9884\u6E32\u67D3\u3002\u5C31\u4F7F\u7528\u8303\u56F4\u800C\u8A00\uFF0C\u5B83\u66F4\u52A0\u8D34\u5408\u4E8E Preact \u6846\u67B6\uFF0C\u4E0E Preact \u672C\u8EAB\u4E00\u6837\u5F3A\u8C03\u7D27\u51D1\u7684\u5927\u5C0F\u3002\u5982\u679C\u4F60\u6B63\u5728\u4F7F\u7528 Preact\uFF0C\u90A3\u4E48 WMR \u53EF\u80FD\u4F1A\u63D0\u4F9B\u66F4\u597D\u7684\u4F53\u9A8C\u3002</p><h2 id="webdev-server" tabindex="-1">@web/dev-server <a class="header-anchor" href="#webdev-server">\xB6</a></h2><p><a href="https://modern-web.dev/docs/dev-server/overview/" target="_blank" rel="noopener noreferrer">@web/dev-server</a>\uFF08\u66FE\u7ECF\u662F <code>es-dev-server</code>\uFF09\u662F\u4E00\u4E2A\u4F1F\u5927\u7684\u9879\u76EE\uFF0C\u57FA\u4E8E koa \u7684 Vite 1.0 \u5F00\u53D1\u670D\u52A1\u5668\u5C31\u662F\u53D7\u5230\u4E86\u5B83\u7684\u542F\u53D1\u3002</p><p><code>@web/dev-server</code> \u9002\u7528\u8303\u56F4\u4E0D\u662F\u5F88\u5E7F\u3002\u5B83\u5E76\u672A\u63D0\u4F9B\u5B98\u65B9\u7684\u6846\u67B6\u96C6\u6210\uFF0C\u5E76\u4E14\u9700\u8981\u4E3A\u751F\u4EA7\u6784\u5EFA\u624B\u52A8\u8BBE\u7F6E Rollup \u914D\u7F6E\u3002</p><p>\u603B\u7684\u6765\u8BF4\uFF0C\u4E0E <code>@web/dev-server</code> \u76F8\u6BD4\uFF0CVite \u662F\u4E00\u4E2A\u66F4\u6CE8\u91CD\u81EA\u8EAB/\u66F4\u9AD8\u5C42\u9762\u7684\u5DE5\u5177\uFF0C\u65E8\u5728\u63D0\u4F9B\u5F00\u7BB1\u5373\u7528\u7684\u5DE5\u4F5C\u6D41\u3002\u8BDD\u867D\u5982\u6B64\uFF0C\u4F46 <code>@web</code> \u8FD9\u4E2A\u9879\u76EE\u7FA4\u5305\u542B\u4E86\u8BB8\u591A\u5176\u4ED6\u7684\u4F18\u79C0\u5DE5\u5177\uFF0C\u4E5F\u53EF\u4EE5\u4F7F Vite \u7528\u6237\u53D7\u76CA\u3002</p>',23),l=[n];function s(p,i,c,d,h,u){return t(),r("div",null,l)}var f=e(a,[["render",s]]);export{g as __pageData,f as default};
